import React, { useState } from 'react';

export function useStaffForm() {
  const [staffData, setStaffData] = useState({});

  const handleOnSubmit = (data) => {
    setStaffData(...data);
  }

  return (
    staffData,
    setStaffData,
    handleOnSubmit
  )
};

export function StaffForm(props) {
  return (
    <form className='staffForm'>
      {props.children}
    </form>
  )
}
