import React, { useState } from 'react';

import { Button, TableCell, TableRow } from '@mui/material';

const CustomButton = (props) => {
  const { backgroundColor, title } = props;
  return (
    <Button
      variant="contained"
      style={{
        width: '110px',
        textTransform: 'none',
        padding: '10px',
        margin: '5px',
        backgroundColor
      }}>{title}</Button>
  )
}

const StyledTableRow = styled(TableRow)(({ theme }) => ({
  '&:nth-of-type(odd)': {
    backgroundColor: theme.palette.action.hover,
  },
  // hide last border
  '&:last-child td, &:last-child th': {
    border: 0,
  },
}));

const Staff = ({ staff, index }) => {
  const [forUpdate, setForUpdate] = useState({});

  return (
    <TableRow>
      { true ? (<TableCell>{ index }</TableCell>) : (<TableCell><input type='text' value={index}></input></TableCell>)}
      <TableCell>{staff.name}</TableCell>
      <TableCell>{staff.surname}</TableCell>
      <TableCell>{staff.asma}</TableCell>
      <TableCell>{staff.unit}</TableCell>
      <TableCell>{staff.rank}</TableCell>
      <TableCell>{staff.specialization}</TableCell>
      <TableCell>{staff.display_name}</TableCell>
      <TableCell>{staff.mobile_phone}</TableCell>
      <TableCell>{staff.work_phone}</TableCell>
      <TableCell>{staff.position_of_responsibility}</TableCell>
      <TableCell>{staff.special_category}</TableCell>
      <TableCell>{staff.indefinitely}</TableCell>
      <TableCell>{staff.mta_payment}</TableCell>
      <TableCell>
        <CustomButton backgroundColor='#03074f' title='Επεξεργασία' />
        <CustomButton backgroundColor="#a40c0c" title='Διαγραφή' />
      </TableCell>
    </TableRow>
  )
}

export default Staff;
