import React, { useState } from 'react';

const useStaffForm = () => {
  const [staffData, setStaffData] = useState({});

  const handleOnSubmit = (data) => {
    setStaffData(...data);
  }

  return (
    staffData,
    setStaffData
  )
}

export default useStaffForm;
