import React, { useState } from "react";
import { Box, Paper, Typography, Tabs, Tab } from "@mui/material";
import { useForm } from "react-hook-form";

import BasicStaffDataGrid from "../../components/Staffs/StaffsCreate/BasicStaffDataGrid";
import FormFooter from "../../components/Staffs/StaffsCreate/FormFooter";
import TabContext from "../../components/Staffs/StaffsCreate/Tabs/TabContext";

const tabHeaders = [
  "Βασικά Στοιχεία",
  "Λοιπά Στοιχεία",
  "Οικογενειακά Στοιχεία",
];

const StaffCreate = () => {
  const {
    register,
    handleSubmit,
    control,
    formState: { errors },
  } = useForm();

  const onSubmit = (data) => {
    // eslint-disable-next-line no-console
    console.log(data);
  };

  const [tabValue, setTabValue] = useState(0);

  const handleTabChange = (event, newTabValue) => {
    setTabValue(newTabValue);
  };

  return (
    <Box component="form" autoComplete="off" onSubmit={handleSubmit(onSubmit)}>
      <Paper sx={{ maxWidth: "900px", m: 3 }} elevation={8}>
        <Tabs value={tabValue} onChange={handleTabChange}>
          {tabHeaders.map((tab, index) => (
            <Tab key={tab} label={tab} value={index} />
          ))}
        </Tabs>
        <TabContext tabValue={tabValue} index={0}>
          <BasicStaffDataGrid
            control={control}
            errors={errors}
            register={register}
          />
        </TabContext>
        <FormFooter />
      </Paper>
      {/* <Paper
        sx={{
          m: 4,
          width: "60rem",
