import React, { useState } from "react";
import { Box, Paper, Typography, Tabs, Tab, useTheme } from "@mui/material";
import { useForm } from "react-hook-form";

import BasicStaffDataGrid from "../../components/Staffs/StaffsCreate/DataGrids/BasicStaffDataGrid";
import FormFooter from "../../components/Staffs/StaffsCreate/FormFooter";
import TabContext from "../../components/Staffs/StaffsCreate/Tabs/TabContext";

const StaffCreate = () => {
  const theme = useTheme();
  const {
    register,
    handleSubmit,
    control,
    formState: { errors },
  } = useForm();

  const onSubmit = (data) => {
    // eslint-disable-next-line no-console
    console.log(data);
  };

  const [tabValue, setTabValue] = useState(0);

  const handleTabChange = (event, newTabValue) => {
    setTabValue(newTabValue);
  };

  return (
    <Box component="form" autoComplete="off" onSubmit={handleSubmit(onSubmit)}>
      <Paper sx={{ width: "50vw", m: 4 }} elevation={8}>
        <Tabs
          TabIndicatorProps={{
            style: {
              background: theme.palette.primary.main,
            },
          }}
          variant="fullWidth"
          value={tabValue}
          onChange={handleTabChange}
        >
          {tabHeaders.map((tab, index) => (
            <Tab
              sx={{
                fontSize: "1.1rem",
                fontWeight: "bold",
                color: "grey",
                bgcolor: "lightgrey",
                textDecoration: "none",
                textTransform: "none",
                "&.Mui-selected": {
                  color: "black",
                },
              }}
              key={tab}
              label={tab}
              value={index}
            />
          ))}
        </Tabs>
        <Box sx={{ mt: 2, ml: 3 }}>
          <Typography variant="h2" fontWeight="700">
            Στοιχεία Προσωπικού
          </Typography>
        </Box>
        <TabContext tabValue={tabValue} index={0}>
          <BasicStaffDataGrid
            control={control}
            errors={errors}
            register={register}
          />
        </TabContext>
        <FormFooter />
      </Paper>
    </Box>
  );
};

export default StaffCreate;
