import React, { useState } from 'react';

import { Button, TableCell, TableRow } from '@mui/material';
import { StyledTableCell, StyledTableRow } from './StyledTableComponents';

import EditIcon from '@mui/icons-material/EditRounded';

const CustomButton = (props) => {
  const { backgroundColor, title } = props;
  return (
    <Button
      variant="contained"
      style={{
        width: '110px',
        textTransform: 'none',
        padding: '10px',
        margin: '5px',
        backgroundColor
      }}>{title}</Button>
  )
}

const Staff = ({ staff, index }) => {
  const [forUpdate, setForUpdate] = useState({});
  return (
    <TableRow>
      { true ? (<TableCell sx={{ fontWeight: 'bold' }}>{ index }</TableCell>) : (<TableCell><input type='text' value={index}></input></TableCell>)}
      <TableCell align='center'>{staff.name}</TableCell>
      <TableCell align='center'>{staff.surname}</TableCell>
      <TableCell align='center'>{staff.asma}</TableCell>
      <TableCell align='center'>{staff.unit}</TableCell>
      <TableCell align='center'>{staff.rank}</TableCell>
      <TableCell align='center'>{staff.specialization}</TableCell>
      <TableCell align='center'>{staff.display_name}</TableCell>
      <TableCell align='center'>{staff.mobile_phone}</TableCell>
      <TableCell align='center'>{staff.work_phone}</TableCell>
      <TableCell align='center'>{staff.position_of_responsibility}</TableCell>
      <TableCell align='center'>{staff.special_category}</TableCell>
      <TableCell align='center'>{staff.mta_payment}</TableCell>
      <TableCell align='center'>
        <Button variant='contained' sx={{
          bgcolor: 'green'
        }}>
          <EditIcon sx={{
            fontSize: 28,
          }} />
        </Button>
        {/* <CustomButton backgroundColor='#03074f' title='Επεξεργασία' /> */}
        <CustomButton backgroundColor="#a40c0c" title='Διαγραφή' />
      </TableCell>
    </TableRow>
  )
}

export default Staff;
