const theme = {
  palette: {
    primary: {
      main: "#03074f",
      slideBar: "#000"
    },
    secondary: {
      main: "#fff",
    },
  },
  typography: {
    h2: {
      fontSize: "1.5rem",
    },
    inputLabel: {
      fontSize: "1rem",
      color: "#03074f",
      fontWeight: "bold",
    },
    h4: {
      color: "white",
      fontSize: "1rem",
      "@media (max-width:600px)": {
        fontSize: "0.8rem",
      },
    },
    navbar: {
      fontSize: "5rem",
    },
  }
}

export default theme;
